import React, { useState, useEffect, useCallback } from 'react';
import {
  Typography,
  Container,
  Paper,
  Box,
  Button,
  CircularProgress,
  Alert,
  TextField,
  Grid,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle
} from '@mui/material';
import { getMyProfile, updateMyProfile, deleteMyAccount } from '../api';
import { useNavigate } from 'react-router-dom';

// Helper function to extract fields based on backend model (referencing genfit-backend/internal/models/user.go)
const getInitialFormData = (profile) => ({
  name: profile?.name || '',
  email: profile?.email || '',
  age: profile?.age || 0,
  sex: profile?.sex || '',
  weight: profile?.weight || 0,
  height: profile?.height || 0,
  current_fitness: {
    activity_level: profile?.current_fitness?.activity_level || '',
    experience_level: profile?.current_fitness?.experience_level || '',
  },
  goals: {
    primary_goal: profile?.goals?.primary_goal || '',
    target_weight: profile?.goals?.target_weight || 0,
    weekly_workouts: profile?.goals?.weekly_workouts || 0,
  },
});

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [formData, setFormData] = useState(getInitialFormData(null));
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [deleteConfirmOpen, setDeleteConfirmOpen] = useState(false);
  const navigate = useNavigate();

  const fetchProfile = useCallback(async () => {
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      const { data } = await getMyProfile();
      setProfile(data);
      setFormData(getInitialFormData(data));
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to fetch profile. Please try again.');
      if (err.response?.status === 401) {
        // Handle token expiration or invalid token scenario
        localStorage.removeItem('genfitAuth'); // Clear auth
        navigate('/login', { state: { from: { pathname: '/profile' } } });
      }
    }
    setLoading(false);
  }, [navigate]);

  useEffect(() => {
    fetchProfile();
  }, [fetchProfile]);

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    const parsedValue = type === 'number' ? parseFloat(value) : value;

    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({ 
        ...prev, 
        [parent]: { ...prev[parent], [child]: parsedValue }
      }));
    } else {
      setFormData(prev => ({ ...prev, [name]: parsedValue }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      // Filter out fields that are not part of the update payload if necessary, or ensure all are sent.
      // The backend /users/me (PUT) expects the same structure as create user but fields are optional.
      const { data } = await updateMyProfile(formData);
      setProfile(data);
      setFormData(getInitialFormData(data));
      setIsEditing(false);
      setSuccess('Profile updated successfully!');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update profile. Please ensure all fields are valid.');
    }
    setLoading(false);
  };

  const handleDeleteAccount = async () => {
    setDeleteConfirmOpen(false);
    setLoading(true);
    setError('');
    try {
      await deleteMyAccount();
      localStorage.removeItem('genfitAuth'); // Clear auth on client
      navigate('/login', { state: { message: 'Account deleted successfully.' } }); // Redirect to login
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to delete account.');
      setLoading(false);
    }
  };

  if (loading && !profile) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="calc(100vh - 200px)">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="md">
      <Paper sx={{ p: { xs: 2, md: 4 }, mt: 3 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          User Profile
        </Typography>

        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}

        {profile && !isEditing && (
          <Box>
            <Typography variant="h6">Name: {profile.name}</Typography>
            <Typography>Email: {profile.email}</Typography>
            <Typography>Age: {profile.age}</Typography>
            <Typography>Sex: {profile.sex}</Typography>
            <Typography>Weight: {profile.weight} kg</Typography>
            <Typography>Height: {profile.height} cm</Typography>

            <Typography variant="h6" sx={{ mt: 2 }}>Current Fitness</Typography>
            <Typography>Activity Level: {profile.current_fitness?.activity_level}</Typography>
            <Typography>Experience Level: {profile.current_fitness?.experience_level}</Typography>

            <Typography variant="h6" sx={{ mt: 2 }}>Goals</Typography>
            <Typography>Primary Goal: {profile.goals?.primary_goal}</Typography>
            <Typography>Target Weight: {profile.goals?.target_weight} kg</Typography>
            <Typography>Weekly Workouts: {profile.goals?.weekly_workouts}</Typography>

            <Box sx={{ mt: 3, display: 'flex', gap: 2 }}>
              <Button variant="contained" onClick={() => setIsEditing(true)} disabled={loading}>
                Edit Profile
              </Button>
              <Button variant="outlined" color="error" onClick={() => setDeleteConfirmOpen(true)} disabled={loading}>
                Delete Account
              </Button>
            </Box>
          </Box>
        )}

        {isEditing && (
          <form onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}><TextField label="Name" name="name" value={formData.name} onChange={handleChange} fullWidth required /></Grid>
              <Grid item xs={12} sm={6}><TextField label="Email" name="email" type="email" value={formData.email} onChange={handleChange} fullWidth required /></Grid>
              <Grid item xs={12} sm={6}><TextField label="Age" name="age" type="number" value={formData.age} onChange={handleChange} fullWidth required InputProps={{ inputProps: { min: 0 } }}/></Grid>
              <Grid item xs={12} sm={6}><TextField label="Sex" name="sex" value={formData.sex} onChange={handleChange} fullWidth /></Grid>
              <Grid item xs={12} sm={6}><TextField label="Weight (kg)" name="weight" type="number" value={formData.weight} onChange={handleChange} fullWidth InputProps={{ inputProps: { step: 0.1, min: 0 } }}/></Grid>
              <Grid item xs={12} sm={6}><TextField label="Height (cm)" name="height" type="number" value={formData.height} onChange={handleChange} fullWidth InputProps={{ inputProps: { step: 0.1, min: 0 } }}/></Grid>

              <Grid item xs={12}><Typography variant="h6" sx={{mt:1}}>Current Fitness</Typography></Grid>
              <Grid item xs={12} sm={6}><TextField label="Activity Level" name="current_fitness.activity_level" value={formData.current_fitness.activity_level} onChange={handleChange} fullWidth /></Grid>
              <Grid item xs={12} sm={6}><TextField label="Experience Level" name="current_fitness.experience_level" value={formData.current_fitness.experience_level} onChange={handleChange} fullWidth /></Grid>

              <Grid item xs={12}><Typography variant="h6" sx={{mt:1}}>Goals</Typography></Grid>
              <Grid item xs={12} sm={6}><TextField label="Primary Goal" name="goals.primary_goal" value={formData.goals.primary_goal} onChange={handleChange} fullWidth /></Grid>
              <Grid item xs={12} sm={6}><TextField label="Target Weight (kg)" name="goals.target_weight" type="number" value={formData.goals.target_weight} onChange={handleChange} fullWidth InputProps={{ inputProps: { step: 0.1, min: 0 } }}/></Grid>
              <Grid item xs={12} sm={6}><TextField label="Weekly Workouts" name="goals.weekly_workouts" type="number" value={formData.goals.weekly_workouts} onChange={handleChange} fullWidth InputProps={{ inputProps: { min: 0 } }}/></Grid>
            </Grid>
            <Box sx={{ mt: 3, display: 'flex', gap: 2 }}>
              <Button type="submit" variant="contained" color="primary" disabled={loading}>
                {loading ? <CircularProgress size={24} /> : 'Save Changes'}
              </Button>
              <Button variant="outlined" onClick={() => { setIsEditing(false); setError(''); setSuccess(''); setFormData(getInitialFormData(profile)); }} disabled={loading}>
                Cancel
              </Button>
            </Box>
          </form>
        )}

        {!profile && !loading && error && (
             <Alert severity="warning" sx={{ mb: 2 }}>Could not load profile data. {error}</Alert>
        )}
      </Paper>

      <Dialog
        open={deleteConfirmOpen}
        onClose={() => setDeleteConfirmOpen(false)}
      >
        <DialogTitle>Confirm Account Deletion</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete your account? This action cannot be undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteConfirmOpen(false)} color="primary" disabled={loading}>
            Cancel
          </Button>
          <Button onClick={handleDeleteAccount} color="error" disabled={loading} autoFocus>
            {loading ? <CircularProgress size={24} /> : 'Delete Account'}
          </Button>
        </DialogActions>
      </Dialog>

    </Container>
  );
};

export default ProfilePage;
